                    manufacturer: this.tyre.manufacturer,
                    year: this.tyre.year,
                    photo: this.tyre.photo
                },
                customTyreBrand: this.customTyreBrand,
                colored: this.colored,
                photo_external_damage: this.photo_external_damage.map(photo => {
                    return {
                        description: photo.description,
                        photo: photo.photo
                    };
                }),
                photo_internal_damage: this.photo_internal_damage.map(photo => {
                    return {
                        description: photo.description,
                        photo: photo.photo
                    };
                }),
                photo_external: this.photo_external.map(photo => {
                    return {
                        description: photo.description,
                        photo: photo.photo
                    };
                }),
                photo_internal: this.photo_internal.map(photo => {
                    return {
                        description: photo.description,
                        photo: photo.photo
                    };
                }),
                functions_check: this.functions_check,
                functions_problems: this.functions_problems,
                comment: this.comment,
                computer_diag: this.computer_diag,
                video: this.video,
                price: {
                    value: this.price.value,
                    currency: this.price.currency
                },
                equipment: this.equipment,
                mileage: this.mileage,
                gearbox: this.gearbox,

                photo_vin: this.photo_vin,
                photo_tech_info: this.photo_tech_info,
            };
            return reportData;

        },

        chooseDetail(detail) {
            this.chosen_detail = detail
            if (!this.colored[detail]) {
                this.colored[detail] = '1'
            }
            console.log(this.colored)
            this.modal_color = true;
        },

        decodeVin() {

            let controlSum = sha1(this.vin + '|decode|7afb5e0f8c22|fac2b495f0').substring(0, 10)
            axios.get('https://api.vindecoder.eu/3.2/7afb5e0f8c22/' + controlSum + '/decode/' + this.vin + '.json').then(response => {
                let info = []
                response.data.decode.forEach(el => {
                    info[el.label] = el.value
                })
                // console.log(info)
                this.brand = info['Make']
                if (!this.brands.includes(this.brand)) {
                    this.customBrand = this.brand
                }
                this.model = info['Model']
                this.year = info['Model Year']
                this.engine_volume = info['Engine Displacement (ccm)'] / 1000
                console.log(info['Transmission'])
            })
        },

        //todo сделать ввод файлов
        previewFile(event) {
            this.loader = true;
            const formData = new FormData();
            formData.append('file', event.target.files[0]);

            axios.post(base_url + 'uploadFile', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            }).then(response => {
                console.log(response.data);
                const file = event.target.files[0];
                const reader = new FileReader();

                // reader.onload = (event) => {
                // };

                reader.readAsDataURL(file);
                let id = response.data.id
                if (event.target.getAttribute('data-subfield'))
                    if (event.target.getAttribute('data-index')) {
                        this[event.target.getAttribute('data-field')][event.target.getAttribute('data-index')][event.target.getAttribute('data-subfield')] = id
                        reader.onload = (e) => {
                            this[event.target.getAttribute('data-field')][event.target.getAttribute('data-index')]['preview'] =
                                e.target.result;
                        };

                        reader.readAsDataURL(file);

                    } else {
                        this[event.target.getAttribute('data-field')][event.target.getAttribute('data-subfield')] = id
                        reader.onload = (e) => {
                            this[event.target.getAttribute('data-field')]['preview'] =
                                e.target.result;
                        };
                    }
                else if (event.target.getAttribute('data-index')) {
                    console.log(event.target.getAttribute('data-field') + ' ' + event.target.getAttribute('data-index'))
                    this[event.target.getAttribute('data-field')][event.target.getAttribute('data-index')] = id
                } else
                    this[event.target.getAttribute('data-field')] = id
            }).catch(error => {
                console.log(error.response.data);
            }).finally(() => {
                this.loader = false;

            });


        },

        previewFiles(event) {
            this.loader = true;

            const uploadedFiles = event.target.files;
            const uploadedFileData = [];

            const processFile = (file, index) => {
                const formData = new FormData();
                formData.append('file', file);

                axios.post(base_url + 'uploadFile', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                }).then(response => {
                    console.log(response.data);
                    const reader = new FileReader();
                    let id = response.data.id;

                    reader.onload = (e) => {
                        uploadedFileData.push({ photo: id, preview: e.target.result });

                        if (uploadedFileData.length === uploadedFiles.length) {
                            this[event.target.getAttribute('data-field')] = this[event.target.getAttribute('data-field')].concat(uploadedFileData);
                        }
                    };

                    reader.readAsDataURL(file);
                }).catch(error => {
                    console.log(error.response.data);
                }).finally(() => {
                    if (index === uploadedFiles.length - 1) {
                        this.loader = false;
                    }
                });
            };

            for (let i = 0; i < uploadedFiles.length; i++) {
                processFile(uploadedFiles[i], i);
            }
        },

        validate(){
            this.errors = []
            if (!this.photo_vin.photo) this.errors.push(this.tr('photo_vin'));
            if (!this.photo_tech_info.photo) this.errors.push(this.tr('photo_tech_info'));
            if (!this.vin) this.errors.push(this.tr('vin'));
            if (!this.brand && !this.customBrand) this.errors.push(this.tr('brand'));
            if (!this.model) this.errors.push(this.tr('model'));
            if (!this.mileage) this.errors.push(this.tr('mileage'));
            if (!this.gearbox) this.errors.push(this.tr('gearbox'));
            if (!this.year) this.errors.push(this.tr('year'));
            if (!this.body) this.errors.push(this.tr('body'));
            // if (!this.body_color) this.errors.push(this.tr('body_color'));
            if (!this.engine_volume) this.errors.push(this.tr('engineVolume'));
            if (!this.engine_power) this.errors.push(this.tr('enginePower'));
            if (!this.drive) this.errors.push(this.tr('drive'));
            if (!this.specification) this.errors.push(this.tr('specification'));
            // if (this.crashes === undefined) this.errors.push(this.tr('crashes'));
            if (this.guarantee_check) {
                if (!this.guarantee.year) this.errors.push(this.tr('guarantee') + this.tr('yearGuarantee'));
                // if (!this.guarantee.month) this.errors.push(this.tr('guarantee') + this.tr('monthGuarantee'));
            }
            if(!this.price.currency) this.errors.push(this.tr('currency'));
            if (!this.price.value) this.errors.push(this.tr('price'))
            if (!this.price.value) this.errors.push(this.tr('comment'))
            return this.errors.length <= 0
        }
    },

    computed: {},

    watch: {
        'photo_vin.photo': function () {
            this.updateReport();
        },
        'photo_tech_info.photo': function () {
            this.updateReport();
