        },
        'tyre.manufacturer': function () {
            this.updateReport();
        },
        'tyre.year': function () {
            this.updateReport();
        },
        'tyre.photo': function () {
            this.updateReport();
        },
        customTyreBrand: function () {
            this.updateReport();
