        colored: function () {
            this.updateReport();
        },
        equipment: {
            handler: function () {
                this.updateReport();
            },
            deep: true
        },
        photo_external_damage: {
            handler: function () {
                this.updateReport();
            },
            deep: true
        },
        photo_internal_damage: {
