        },
        functions_problems: function () {
            this.updateReport();
        },
        comment: function () {
            this.updateReport();
        },
        computer_diag: function () {
            this.updateReport();
        },
        video: function () {
            this.updateReport();
        },
        'price.value': function () {
            this.updateReport();
        },
        'price.currency': function () {
            this.updateReport();
        }
    }
}
